import React,{useState} from 'react'
import {
    AppBar,
    Toolbar,
    ListItem,
    IconButton,
    ListItemText,
    Avatar,
    Divider,
    List,
    Typography,
    Box,
    ListItemIcon
} from "@material-ui/core";
import{
    ArrowBack,
    AssignmentInd,
    Home,
    Apps,
    ContactMail
} from "@material-ui/icons";
import silasImage from '../images/Silas.jpg'
import { makeStyles } from '@material-ui/core/styles';
import MobileRightMenuSilder from "@material-ui/core/Drawer"

// CSS styles
const useStyle = makeStyles(theme=>({
    menuSilderContainer:{
        widht:250,
        background:"#511",
        height:"30rem",
    },
    avatar:{
        display:"block",
        margin:'0.5rem auto',
        width:theme.spacing(13),
        height:theme.spacing(13),
    },
    listItem:{
        color:"tan"
    }
}));

const menuItems = [
    {
        listIcon: <Home/>,
        listText:"Home"
    },
    {
        listIcon: <AssignmentInd/>,
        listText:"Resume"
    },
    {
        listIcon: <Apps/>,
        listText:"Portfolio"
    },
    {
        listIcon: <ContactMail/>,
        listText:"Contacts"
    }
]

// this function is the entra for the parent class
export const Navbar = () => { 

    const [state,setState] = useState({
        right:false
    });

    const toggleSilder = (slider,open) => () => {
        setState({ ...state, [slider]: open });
        }


    const classes = useStyle()

    const sideList = silder => (
        <Box className={classes.menuSilderContainer} compoent = "div">
        {/* the avatart is the component for showing  a image */}
            <Avatar className={classes.avatar} src = {silasImage} alt="this is the photo of Silas" />
            <Divider/>
            <List>
                {menuItems.map((listItem,key)=> (      
                   <ListItem button key = {key}>
                       <ListItemIcon  className={classes.listItem}> {listItem.listIcon}</ListItemIcon>
                       <listText  className={classes.listItem}> {listItem.listText} </listText>
                   </ListItem>
                ))}
            </List>
        </Box>
    )

    return (
    <>
        <Box component="nav">
            <AppBar position="static" style={{background:'#222'}}>
                <Toolbar>
                    <IconButton onClick={toggleSilder("right",true)}>
                        <ArrowBack style={{color:"tomato"}}/>
                    </IconButton>
                    <Typography variant='h5' style={{color:"tan"}}>
                        Porfolio
                    </Typography>
                    <MobileRightMenuSilder open={state.right}>
                        {sideList("ritht")}
                    </MobileRightMenuSilder>
                </Toolbar>
            </AppBar>
        </Box>
    </>
    )
}

export default Navbar;
